class SBWriter{constructor(t){this.word=t,this.txt=t.innerText,this.len=this.txt.length,this.time}setTime(t){return this.time=t,this}typewriter(){let t=t=>{let e=t.length,s=0,h=setInterval(()=>{this.word.innerHTML+=t[s],++s==e&&(clearInterval(h),i())},this.time)},i=()=>{let i=this.len,e=this.word.innerText,s=setInterval(()=>{let h=this.txt.slice(0,i-1);this.word.innerText=h,--i<1&&(clearInterval(s),t(e))},this.time)};i()}fadeEffect(){setInterval(()=>{this.word.style.transition="0.8s",this.word.style.opacity="0",setTimeout(()=>{this.word.style.opacity="1"},this.time/2)},this.time)}typewriter2(){this.word.setAttribute("style",`
  display:inline-block; overflow:hidden; text-wrap:nowrap
  `);let t=this.word.getBoundingClientRect().width,i=t,e=()=>{let i=0,e=setInterval(()=>{this.word.style.width=`${i}px`,++i>parseInt(t)+10&&(clearInterval(e),s())},this.time)},s=()=>{let s=setInterval(()=>{this.word.style.width=`${parseInt(i)}px`,--i<0&&(clearInterval(s),i=t,e())},this.time)};s()}}